    {% set color = colorInterpolate([ 'green', 'red' ], tags.voltage / 1000) %}
  {% else %}
    {% set color = colorInterpolate([ 'red', 'blue' ], (tags.voltage - 1000) / 24000) %}
  {% endif %}
{% else %}
  {% set color='black' %}
{%endif %}

