query.13:
(
node[highway~"^(elevator)$"];
node[information];
node[tourism~"^(viewpoint)$"];
node[man_made~"^(cairn)$"];
node[amenity~"^(shelter)$"];
way[amenity~"^(shelter)$"];
relation[amenity~"^(shelter)$"];
