query.17:
(
node[highway~"^(crossing|elevator)$"];
node[information];
node[tourism~"^(viewpoint)$"];
node[man_made~"^(cairn)$"];
node[amenity~"^(shelter)$"];
way[amenity~"^(shelter)$"];
